import React from 'react';
import PropTypes from 'prop-types';
import './button.css';

/**
 * Primary UI component for user interaction
 */


export const  Button = ({ primary, backgroundColor, size, label,buttonIcon, menu, ...props }) => {
  const mode = primary ? 'storybook-button--primary' : 'storybook-button--secondary';
  const inline = menu ? 'storybook-button--block' : '';

if (props.min){
  return <></>;
}

  return (
    <button
      type="button"
      className={['storybook-button', `storybook-button--${size}`, mode, inline].join(' ')}
      style={backgroundColor && { backgroundColor }}
      {...props}
    >
      {inline?"":<span>{label}</span>}
      {buttonIcon!==null?<span>{buttonIcon}</span>:""}

      {!inline?"":<span>{label}{props.min?"T":"F"}</span>}
    </button>
  );
};

Button.propTypes = {
  /**
   * Is this the principal call to action on the page?
   */
  primary: PropTypes.bool,
  /**
   * What background color to use
   */
  backgroundColor: PropTypes.string,
  buttonIcon: PropTypes.string,
  /**
   * How large should the button be?
   */
  size: PropTypes.oneOf(['small', 'medium', 'large']),
  /**
   * Button contents
   */
  label: PropTypes.string.isRequired,
  /**
   * Optional click handler
   */
  onClick: PropTypes.func,
};

Button.defaultProps = {
  backgroundColor: null,
  primary: true,
  size: 'medium',
  onClick: undefined,
  buttonIcon: null,
};
