import React from 'react';

import {Button} from './Button';


export default function SideMenu ({ minimised, buttons }) 
{
 
  if (minimised) {
    return  (
    <div className="side-menu">
      <div className="menu-toggle-container menu-toggle-container--minimised ">
    <button className="menu-toggle menu-toggle--open"></button>
</div>
    </div> );
  }

  if (buttons.length === 0) {
    return <div className="side-menu">empty</div>;
  }

  return (
    <div className="side-menu">
    
        <div className="menu-toggle-container">
            <button className="menu-toggle menu-toggle--open"></button>
        </div>
      {buttons.map((button,index) => (<>
  <Button key={button.id} {...button} />
        </>
      ))}
    </div>
  );
}
